mapper.select:
SELECT USER_NAME
FROM TB_USER
WHERE 1=1
AND PASSWORD = #{password} 
AND USER_ID = #{userId}

mapper.insert:
INSERT INTO TB_BOARD
	( ID
	  , NAME
	  , DESCRIPTION
	  , USE_YN
	  , REG_USER )
VALUES ( #{id}
	  , #{name}
	  , #{description}
	  , #{useYn}
	  , #{regUser} )

mapper.update:
UPDATE TB_BOARD
SET ID=#{id}
	, NAME=#{name}
	, DESCRIPTION=#{description}
	, USE_YN=#{useYn}
	  WHERE ID=#{id}
